void setup(){  Serial.begin(9600);  pinMode(1, OUTPUT);  pinMode(2, OUTPUT);  pinMode(3, OUTPUT);  pinMode(4, OUTPUT);  pinMode(10, OUTPUT);  pinMode(11, OUTPUT);  pinMode(12, OUTPUT);  pinMode(13, OUTPUT);  pinMode(8,OUTPUT);  pinMode(9,OUTPUT);  digitalWrite(8,HIGH);  digitalWrite(9,HIGH);  digitalWrite(10,LOW);  digitalWrite(11,LOW);  digitalWrite(12,LOW);  digitalWrite(13,LOW);}byte income =0;void loop(){if(Serial.available()>0)  {    int s[3];    s[0]=10;    s[1]=11;    s[2]=12;    s[3]=13;         for(int i=3;i=0;i--)    {     	       digitalWrite(s[3-i],LOW);    }    for(int i=3;i=0;i--)    {     	  income=Serial.read();          income=income-'0';    	digitalWrite(2,income&0x1);    	digitalWrite(3,(income>>1)&0x1);    	digitalWrite(4,(income>>2)&0x1);    	digitalWrite(5,(income>>3)&0x1);      delay(10);      digitalWrite(s[3-i],HIGH);    }  }}